INSERT INTO jos_zoo_search_index VALUES("86","d7c483ed-b6d6-466d-b389-29dfbaa7f576","My vision for the Austin Stone Community Church is a church where each person, regardless of age, stage of life, or background, can experience authentic worship, have genuine community with others, and be equipped to minister to and serve others. My prayer is that God will use the Austin Stone to build His Kingdom in Austin and in the world, and that the partners of the Austin Stone will be known by their love – their love for God, for each other, and for the world around them.");
